import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css',
  ]
})
export class HomeComponent {
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>HRD Exam</title>
  <link rel="stylesheet" href="home.component.css">
  <style>
    body, html {
      height: 100%;
      margin: 0;
      font-family: Arial, sans-serif;
    }

    .background-container {
      background-image: url('../../assets/'); /* Path to your image */
      background-size: cover;
      background-repeat: no-repeat;
      background-position: center;
      height: 100vh;
      width: 100%;
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      color: white;
      text-align: center;
    }

    .btnItem {
      margin-top: 20px;
    }

    .btnItem button {
      margin: 5px;
    }
  </style>
</head>
<body>
  <div class="background-container">
    <div id="section-container" class="row m-0">
      <div id="section-hrd-exam-text" class="col-lg-6 col-md-6 col-sm-12 font-face-en text-white text-center pb-5 bgPrimaryColor">
        <h1>HRD Exam</h1>
        <h5>Korea Software HRD Center</h5>
        <div id="buttonContainer"></div>
      </div>
      <div id="section-hrd-exam-logo" class="col-lg-6 col-md-6 col-sm-12 bgPrimaryColor"></div>
    </div>
    <div class="row m-0 px-5 pt-5">
      <div class="col-lg-6 col-md-6 col-sm-12 m-auto">
        <div class="card cardColumnStyle">
          <div class="card-header cardHeaderStyle">
            <img width="170" class="rounded-circle m-auto" alt="graduate-hat" src="assets/hatImage.jpg">
          </div>
          <div class="card-body font-face-kh" style="line-height: initial;">
            <h5 class="card-title title">ការធ្វើតេស្ត Online</h5>
            <h5 class="card-title title">បង្កើតឡើងសម្រាប់សិស្ស HRD</h5>
          </div>
        </div>
      </div>
    </div>
    <div class="row m-0 p-5">
      <div class="col-lg-6 col-md-6 col-sm-12 mb-5">
        <div class="card cardStyle">
          <div class="card-header cardHeaderStyle">
            <img roundedCircle width="170" class="cardImgStyle" alt="computer" src="assets/computer.jpg">
          </div>
          <div class="card-body textContainer font-face-kh" style="line-height: initial;">
            <h5 class="card-title title">ការប្រលង IT</h5>
            <p class="card-text description">
              បានផ្តល់នៅសំណួរជាច្រើនប្រភេទដែលមានភាព ងាយស្រួលក្នុងការបង្កើតសំនួរសម្រាប់ការប្រលង
            </p>
          </div>
        </div>
      </div>
      <div class="col-lg-6 col-md-6 col-sm-12 mb-5">
        <div class="card cardStyle">
          <div class="card-header cardHeaderStyle">
            <img roundedCircle class="cardImgStyle" alt="korean-flag" src="assets/koreanFlag.jpg">
          </div>
          <div class="card-body textContainer font-face-kh" style="line-height: initial;">
            <h5 class="card-title title">ការប្រលងជាភាសាកូរ៉េ</h5>
            <p class="card-text description">
              បានផ្តល់នៅសំណួរជាច្រើនប្រភេទដែលមានភាព ងាយស្រួលក្នុងការបង្កើតសំនួរសម្រាប់ការប្រលង
            </p>
          </div>
        </div>
      </div>
    </div>
    <!-- Footer and modals here -->
  </div>
</body>
</html>
