import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css',
  ]
})
export class HomeComponent {
}
<div class="custom">
  <div class="align-items-center justify-content-center vh-100 background-container text-white" >
    <div class="text-center mb-5 ">
      <img src="assets/logo.png" alt="exam logo" class="mb-4">
      <h1>HRD Exam</h1>
      <p>Korea Software HRD Center</p>
      <div class="my-4">
        <button class="btn btn-light btn-lg mx-2">Start Quiz</button>
        <button class="btn btn-light btn-lg mx-2">Register</button>
      </div>
    </div>
  </div>
</div>
